import React, { useState, useEffect } from 'react';
import './style/game.css'; // Adjust the file path based on your project structure
import hearts from './image/heart.png';
import heart164 from './image/sirds.png';
import spade from './image/spade.png';
import diamond from './image/diamond.png';
import diamondSide from './image/diamond-side.png';
import Clubs from './image/clubs.png';

const Game = () => {
  const [cards, setCards] = useState([
    { id: 1, img: hearts, text: 'A', sideImg: heart164, customText: 'A', isFlipped: false, matched: false },
    { id: 2, img: hearts, text: 'A', sideImg: heart164, customText: 'A', isFlipped: false, matched: false },
    { id: 3, img: hearts, text: 'A', sideImg: heart164, customText: 'A', isFlipped: false, matched: false },
    { id: 4, img: spade, text: 'A', sideImg: spade, customText: 'A', isFlipped: false, matched: false },
    { id: 5, img: spade, text: 'A', sideImg: spade, customText: 'A', isFlipped: false, matched: false },
    { id: 6, img: spade, text: 'A', sideImg: spade, customText: 'A', isFlipped: false, matched: false },
    { id: 7, img: diamond, text: 'A', sideImg: diamondSide, customText: 'A', isFlipped: false, matched: false },
    { id: 8, img: diamond, text: 'A', sideImg: diamondSide, customText: 'A', isFlipped: false, matched: false },
    { id: 9, img: diamond, text: 'A', sideImg: diamondSide, customText: 'A', isFlipped: false, matched: false },
    { id: 10, img: Clubs, text: 'A', sideImg: Clubs, customText: 'A', isFlipped: false, matched: false },
    { id: 11, img: Clubs, text: 'A', sideImg: Clubs, customText: 'A', isFlipped: false, matched: false },
    { id: 12, img: Clubs, text: 'A', sideImg: Clubs, customText: 'A', isFlipped: false, matched: false },
  ]);

  const [isGameFinished, setIsGameFinished] = useState(false);
  const [correctMatches, setCorrectMatches] = useState(0);
  const [selectedCards, setSelectedCards] = useState([]);
  const [incorrectAttempts, setIncorrectAttempts] = useState(0);

  useEffect(() => {
    setCards((cards) => shuffleArray(cards));
  }, []);

  function shuffleArray(array) {
    const shuffledArray = array.slice();
    for (let i = shuffledArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
    }
    return shuffledArray;
  }

  const handleCardClick = (id) => {
    if (isGameFinished || selectedCards.length >= 3) {
      // Game is already finished or 3 cards are already selected, do nothing
      return;
    }

    setCards((prevCards) =>
      prevCards.map((card) =>
        card.id === id && !card.isFlipped ? { ...card, isFlipped: true } : card
      )
    );

    setSelectedCards((prevSelected) => [...prevSelected, id]);

    // Check if three cards are selected
    if (selectedCards.length === 2) {
      const selectedCardTypes = selectedCards.map((cardId) => {
        const selectedCard = cards.find((card) => card.id === cardId);
        return selectedCard.customText;
      });

      // If the selected cards are not identical, increase incorrect attempts
      if (selectedCardTypes[0] !== selectedCardTypes[1]) {
        setIncorrectAttempts((prevAttempts) => {
          const updatedAttempts = prevAttempts + 1;
          // Check if three consecutive incorrect attempts have been made
          if (updatedAttempts >= 2) {
            setTimeout(() => {
              setCards((prevCards) =>
                prevCards.map((card) =>
                  card.isFlipped && !card.matched ? { ...card, isFlipped: false } : card
                )
              );
              setSelectedCards([]); // Reset selected cards after flipping back
              setIncorrectAttempts(0); // Reset incorrect attempts after flipping back
            }, 1000); // Delay for a moment before flipping back
          }
          return updatedAttempts;
        });
      } else {
        // If the selected cards are identical, mark them as matched
        setCards((prevCards) =>
          prevCards.map((card) =>
            selectedCards.includes(card.id) ? { ...card, matched: true } : card
          )
        );
        setCorrectMatches((prevMatches) => prevMatches + 1);

        // Reset selected cards and incorrect attempts on a correct match
        setSelectedCards([]);
        setIncorrectAttempts(0);

        // Check if the required number of correct matches is reached
        if (correctMatches + 1 === 3) {
          setIsGameFinished(true);
          // Handle game completion (e.g., show a message or perform any other action)
        }
      }
    }
  };

  return (
    <div className="game-main">
      <div className="game-buttons">
        <button>HINT</button>
        <div className="timer-container">02:69</div>
        <button>GIVE UP</button>
      </div>
      <div className="card-container">
        {cards.map((card) => (
          <div
            className={`card1 ${card.isFlipped ? 'flipped' : ''}`}
            key={card.id}
            onClick={() => handleCardClick(card.id)}
          >
            <div
              className={`card-text ${card.isFlipped ? 'card-text-second' : 'card-text-back'} ${
                card.id === 4 || card.id === 5 || card.id === 6 ? 'spade-specific-styling' : ''
              }`}
            >
              {card.isFlipped ? (
                <>
                  {card.text}
                  <img src={card.sideImg} alt={card.alt} />
                </>
              ) : (
                '?'
              )}
            </div>
            <div className={`heart-icon ${card.isFlipped ? 'bottom-right-rotate' : ''}`}>
              <img
                className={`hearts ${card.isFlipped ? 'bottom-right-rotate' : ''}`}
                src={card.isFlipped ? card.img : ''}
                alt={card.alt}
              />
            </div>
            <div
              className={`card-text-first ${card.isFlipped ? 'rotate-text' : ''} ${
                card.id === 4 || card.id === 5 || card.id === 6 ? 'special-text' : ''
              } ${
                card.id === 4
                  ? 'top-left-rotate'
                  : card.id === 5
                  ? 'top-left-rotate'
                  : card.id === 6
                  ? 'top-left-rotate'
                  : ''
              }`}
            >
              {card.isFlipped ? (
                <>
                  {card.id === 4 || card.id === 5 || card.id === 6 ? (
                    <p className="special-text">{card.customText}</p>
                  ) : (
                    card.customText
                  )}
                  <img
                    className={`bottom-right-image ${
                      card.id === 4 || card.id === 5 || card.id === 6 ? 'bottom-right-custom' : ''
                    }`}
                    src={card.isFlipped ? card.sideImg : ''}
                    alt={card.alt}
                  />
                </>
              ) : null}
            </div>
            {/* Add condition to apply border class only when not flipped */}
            <div className={`border-container ${!card.isFlipped ? 'no-border' : ''}`}>
              {/* Your border content here */}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Game;
