// Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './style/styles.css'; // Adjust the file path based on your project structure
import { GoogleOAuthProvider } from '@react-oauth/google';

const Login = () => {
  const navigate = useNavigate(); // Place the hook here
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState(null);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost/game/login.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
        credentials: 'omit',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();

      if (result.success) {
        console.log('Login successful');

        localStorage.setItem('loggedInUser', JSON.stringify({
          id: result.userData.id,
          username: result.userData.username,
          email: result.userData.email,
        }));

        sessionStorage.setItem('userId', result.userData.id);

        console.log('userId:', result.userData.id);

        localStorage.setItem('isLoggedIn', 'true');

        navigate('/levels');
      } else {
        console.error('Login failed:', result.message);
      }

      setLoginStatus({
        success: result.success,
        message: result.message,
      });
    } catch (error) {
      console.error('Error:', error);

      setLoginStatus({
        success: false,
        message: 'Error during login. Please try again later.',
      });
    }
  };

  const handleGoogleLogin = async () => {
    try {
      const googleProvider = new GoogleOAuthProvider({
        clientId: '301743701977-0jo8mmnfhqigsk8i1bpti9m6vv50rvgt.apps.googleusercontent.com', // Replace with your actual client ID
      });

      const result = await googleProvider.signIn();

      console.log('Google Sign-In Result:', result);

      navigate('/levels'); // Use navigate directly here
    } catch (error) {
      console.error('Google Sign-In Error:', error);
    }
  };

  const handleRegister = () => {
    navigate('/register');
  };

  return (
<div className="login-main">
      <h2>Login</h2>
      <form>
        <input type="email" value={email} onChange={handleEmailChange} placeholder="Email" />
        <br />
        <input type="password" value={password} onChange={handlePasswordChange} placeholder="Password" />
        <br />
        <div className="button">
          <button type="button" onClick={handleLogin}>Login</button>
        </div>
      </form>

      <div className="button">
        <button type="button" onClick={handleGoogleLogin}>Login with Google</button>
      </div>

      {loginStatus && (
        <div className={`login-message ${loginStatus.success ? 'success' : 'error'}`}>
          {loginStatus.success ? 'Login successful' : loginStatus.message}
        </div>
      )}

      <div className="register-link">
        Not a user? <a href="/register" onClick={handleRegister}>Register now</a>
      </div>
    </div>
  );
};

export default Login;
