import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import './style/styles.css'; // Adjust the file path based on your project structure

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState(null);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost/game/login.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
        credentials: 'omit',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();

      if (result.success) {
        console.log('Login successful');

        localStorage.setItem('loggedInUser', JSON.stringify({
          id: result.userData.id,
          username: result.userData.username,
          email: result.userData.email,
        }));

        sessionStorage.setItem('userId', result.userData.id);

        console.log('userId:', result.userData.id);

        localStorage.setItem('isLoggedIn', 'true');

        navigate('/levels');
      } else {
        console.error('Login failed:', result.message);
      }

      setLoginStatus({
        success: result.success,
        message: result.message,
      });
    } catch (error) {
      console.error('Error:', error);

      setLoginStatus({
        success: false,
        message: 'Error during login. Please try again later.',
      });
    }
  };

  const handleRegister = () => {
    navigate('/register');
  };

  const handleGoogleLoginSuccess = (response) => {
    console.log('Google login success:', response);

    // Check if the response contains the email information
    if (response && response.profileObj && response.profileObj.email) {
      // Extract necessary information from the response
      const { profileObj } = response;
      const { email } = profileObj;

      // Generate a random username using the first letters of the email
      const username = email.split('@')[0].split('.').map(word => word[0]).join('');

      // Save user data in the session
      sessionStorage.setItem('loggedInUser', JSON.stringify({
        id: 'google', // You can use a unique identifier for Google login users
        username: username,
        email: email,
      }));

      // Redirect to the levels page or perform other actions
      navigate('/levels');
    } else {
      console.error('Email not found in Google authentication response');
      // Display an error message to the user
      // For example, set an error state to show an error message in the UI
      setLoginStatus({
        success: false,
        message: 'Email information is required for authentication. Please try again or use a different authentication method.',
      });
    }
  };




  const handleGoogleLoginFailure = (error) => {
    console.error('Google login failed:', error);
  };

  return (
    <div className="login-main">
      <h2>Login</h2>
      <form>
        <input type="email" value={email} onChange={handleEmailChange} placeholder="Email" />
        <br />
        <input type="password" value={password} onChange={handlePasswordChange} placeholder="Password" />
        <br />
        <div className="button">
          <button type="button" onClick={handleLogin}>
            Login
          </button>
        </div>
      </form>

      {loginStatus && (
        <div className={`login-message ${loginStatus.success ? 'success' : 'error'}`}>
          {loginStatus.success
            ? 'Login successful'
            : loginStatus.message}
        </div>
      )}

      <div className="google-login">
        <GoogleOAuthProvider clientId="916183960867-podcm2n2nil1q66r019e53o02d8obb2h.apps.googleusercontent.com">
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess}
            onFailure={handleGoogleLoginFailure}
            buttonText="Login with Google"
          />
        </GoogleOAuthProvider>
      </div>

      <div className="register-link">
        Not a user? <a href="/register" onClick={handleRegister}>Register now</a>
      </div>
    </div>
  );
};

export default Login;
