import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import './style/styles.css'; // Adjust the file path based on your project structure

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState(null);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = async () => {
    try {
      // Your login logic here
    } catch (error) {
      console.error('Error:', error);
      setLoginStatus({
        success: false,
        message: 'Error during login. Please try again later.',
      });
    }
  };

  const handleRegister = () => {
    navigate('/register');
  };

  const handleGoogleLoginSuccess = (response) => {
    console.log('Google login success:', response);

    // Check if the response contains the necessary fields
    if (response && response.credential && response.clientId) {
      // Extract necessary information from the response
      const { credential, clientId } = response;

      // Decode the credential if necessary
      const decodedCredential = decodeCredential(credential);

      // Extract the email from the decoded credential or other available fields
      const email = decodedCredential?.email || response?.profileObj?.email || null;

      if (email) {
        // Proceed with your login logic using the email
        console.log('Email found:', email);
      } else {
        console.error('Email not found in Google authentication response');
        // Handle the scenario where email information is not available
      }
    } else {
      console.error('Incomplete Google authentication response:', response);
      // Handle the scenario where response is incomplete
    }
  };

  // Function to decode the JWT credential if necessary
  const decodeCredential = (credential) => {
    // Implement your decoding logic here
    // Return the decoded credential or null if unable to decode
  };


  const handleGoogleLoginFailure = (error) => {
    console.error('Google login failed:', error);
  };

  return (
    <div className="login-main">
      <h2>Login</h2>
      <form>
        <input type="email" value={email} onChange={handleEmailChange} placeholder="Email" />
        <br />
        <input type="password" value={password} onChange={handlePasswordChange} placeholder="Password" />
        <br />
        <div className="button">
          <button type="button" onClick={handleLogin}>
            Login
          </button>
        </div>
      </form>

      {loginStatus && (
        <div className={`login-message ${loginStatus.success ? 'success' : 'error'}`}>
          {loginStatus.success
            ? 'Login successful'
            : loginStatus.message}
        </div>
      )}

      <div className="google-login">
        <GoogleOAuthProvider clientId="916183960867-podcm2n2nil1q66r019e53o02d8obb2h.apps.googleusercontent.com">
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess}
            onFailure={handleGoogleLoginFailure}
            buttonText="Login with Google"
          />
        </GoogleOAuthProvider>
      </div>

      <div className="register-link">
        Not a user? <a href="/register" onClick={handleRegister}>Register now</a>
      </div>
    </div>
  );
};

export default Login;
