// Game.js
import React, { useState, useEffect } from 'react';
import './style/game.css'; // Adjust the file path based on your project structure
import Card from './Card'; // Import the Card component
import hearts from './image/heart.png';
import heart164 from './image/sirds.png';
import spade from './image/spade.png';
import diamondSide from './image/diamond-side.png';
import Clubs from './image/clubs.png';

const Game = () => {
  const [shuffledCards, setShuffledCards] = useState([]);

  useEffect(() => {
    // Define your cards
    const cards = [
      { id: 1, suit: 'heart', img: heart164 },
      { id: 2, suit: 'spade', img: spade },
      { id: 3, suit: 'diamond', img: diamondSide },
      { id: 4, suit: 'clubs', img: Clubs },
      // Add more cards as needed
    ];

    // Duplicate each card to have 3 of each
    const duplicatedCards = [...cards, ...cards, ...cards];

    // Shuffle the cards randomly
    const shuffled = duplicatedCards.sort(() => Math.random() - 0.5);

    setShuffledCards(shuffled);
  }, []); // Run only once on component mount

  return (
    <div className="game-main">
      <div className="game-buttons">
        <button>HINT</button>
        <div className="timer-container">02:69</div>
        <button>GIVE UP</button>
      </div>
      <div className="card-container">
        {shuffledCards.map((card) => (
          <Card key={card.id} suit={card.suit} img={card.img} />
        ))}
      </div>
    </div>
  );
};

export default Game;
