import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from 'react-google-login';
import './style/styles.css'; 

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState(null);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleGoogleLoginSuccess = (googleUser) => {
    const idToken = googleUser.getAuthResponse().id_token;
    // Send idToken to your server for verification and user authentication
    // Example:
    // fetch('http://localhost/game/google_login.php', {
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify({ idToken }),
    // })
    // .then(response => response.json())
    // .then(data => {
    //   // Handle response from server
    // })
    // .catch(error => {
    //   console.error('Error:', error);
    // });
  };

  const handleGoogleLoginFailure = (error) => {
    console.error('Google Login Error:', error);
  };

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost/game/login.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
        credentials: 'omit',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();

      if (result.success) {
        localStorage.setItem('loggedInUser', JSON.stringify({
          id: result.userData.id,
          username: result.userData.username,
          email: result.userData.email,
        }));

        sessionStorage.setItem('userId', result.userData.id);

        localStorage.setItem('isLoggedIn', 'true');

        navigate('/levels');
      } else {
        console.error('Login failed:', result.message);
      }

      setLoginStatus({
        success: result.success,
        message: result.message,
      });
    } catch (error) {
      console.error('Error:', error);

      setLoginStatus({
        success: false,
        message: 'Error during login. Please try again later.',
      });
    }
  };

  const handleRegister = () => {
    navigate('/register');
  };

  return (
    <div className="login-main">
      <h2>Login</h2>
      <form>
        <input type="email" value={email} onChange={handleEmailChange} placeholder="Email" />
        <br />
        <input type="password" value={password} onChange={handlePasswordChange} placeholder="Password" />
        <br />
        <div className="button">
          <button type="button" onClick={handleLogin}>
            Login
          </button>
        </div>
      </form>

      {loginStatus && (
        <div className={`login-message ${loginStatus.success ? 'success' : 'error'}`}>
          {loginStatus.success
            ? 'Login successful'
            : loginStatus.message}
        </div>
      )}

      <div className="register-link">
        Not a user? <a href="/register" onClick={handleRegister}>Register now</a>
      </div>

      <GoogleLogin
        clientId="916183960867-podcm2n2nil1q66r019e53o02d8obb2h.apps.googleusercontent.com"
        buttonText="Login with Google"
        onSuccess={handleGoogleLoginSuccess}
        onFailure={handleGoogleLoginFailure}
        cookiePolicy={'single_host_origin'}
      />
    </div>
  );
};

export default Login;
