import React, { useState } from 'react';
import './style/game.css'; // Adjust the file path based on your project structure
import hearts from './image/heart.png'
import heart164 from './image/sirds.png'
import spade from './image/spade.png'
import diamond from './image/diamond.png'
import diamondSide from './image/diamond-side.png'
import Clubs from './image/clubs.png'

const Game = () => {
  const [cards, setCards] = useState([
    { id: 1, image: hearts, flipped: false, matched: false },
    { id: 2, image: heart164, flipped: false, matched: false },
    { id: 3, image: spade, flipped: false, matched: false },
    // Add more cards as needed
  ]);
  const [flippedCards, setFlippedCards] = useState([]);

  const handleCardClick = (id) => {
    setCards(cards.map(card => card.id === id ? { ...card, flipped: !card.flipped } : card));
    setFlippedCards([...flippedCards, id]);
    if (flippedCards.length === 3) {
      checkForMatch();
    }
  };

  const checkForMatch = () => {
    if (flippedCards.length === 3) {
      const [card1, card2, card3] = flippedCards;
      if (cards[card1].image === cards[card2].image && cards[card1].image === cards[card3].image) {
        setCards(cards.map(card => flippedCards.includes(card.id) ? { ...card, matched: true } : card));
      }
      setFlippedCards([]);
    }
  };

  return (
    <div className="game-main">
      <div className='game-buttons'>
        <button>HINT</button>
        <div className="timer-container">
          02:69
        </div>
        <button>GIVE UP</button>
      </div>
      <div className="card-container">
        {cards.map((card, index) => (
          <div key={index} className='card1' onClick={() => handleCardClick(card.id)}>
            <div className="card-text-second">A
              <img src={card.image} />
            </div>
            <div className="heart-icon">
              <img className='hearts' src={card.image} alt="Icon" />
            </div>
            <div className="card-text-first">A 
              <img src={card.image} />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Game;
