import React, { useState, useEffect } from 'react';
import './style/game.css'; // Adjust the file path based on your project structure
import hearts from './image/heart.png';
import heart164 from './image/sirds.png';
import spade from './image/spade.png';
import diamond from './image/diamond.png';
import diamondSide from './image/diamond-side.png';
import Clubs from './image/clubs.png';
import heartIcon from './image/heart-icon.png'; // Import your custom icon for hearts
import spadeIcon from './image/spade-icon.png'; // Import your custom icon for spades
import diamondIcon from './image/diamond-icon.png'; // Import your custom icon for diamonds
import clubsIcon from './image/clubs-icon.png'; // Import your custom icon for clubs

const Game = () => {
  const [cards, setCards] = useState([
    { id: 1, text: 'A', sideImg: heart164, customText: 'A', isFlipped: false },
    { id: 2, text: 'A', sideImg: heart164, customText: 'A', isFlipped: false },
    { id: 3, text: 'A', sideImg: heart164, customText: 'A', isFlipped: false },
    { id: 4, text: 'A', sideImg: spade, customText: 'A', isFlipped: false },
    { id: 5, text: 'A', sideImg: spade, customText: 'A', isFlipped: false },
    { id: 6, text: 'A', sideImg: spade, customText: 'A', isFlipped: false },
    { id: 7, text: 'A', sideImg: diamondSide, customText: 'A', isFlipped: false },
    { id: 8, text: 'A', sideImg: diamondSide, customText: 'A', isFlipped: false },
    { id: 9, text: 'A', sideImg: diamondSide, customText: 'A', isFlipped: false },
    { id: 10, text: 'A', sideImg: Clubs, customText: 'A', isFlipped: false },
    { id: 11, text: 'A', sideImg: Clubs, customText: 'A', isFlipped: false },
    { id: 12, text: 'A', sideImg: Clubs, customText: 'A', isFlipped: false },
  ]);

  useEffect(() => {
    setCards((cards) => shuffleArray(cards));
  }, []);

  function shuffleArray(array) {
    const shuffledArray = array.slice(); // Create a copy of the array to avoid mutating the original array
    for (let i = shuffledArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
    }
    return shuffledArray;
  }

  const getIconForSuit = (suit) => {
    const suitIcons = {
      'hearts': heartIcon,
      'spade': spadeIcon,
      'diamond': diamondIcon,
      'Clubs': clubsIcon,
    };
    return suitIcons[suit.toLowerCase()];
  };

  const handleCardClick = (id) => {
    setCards((prevCards) =>
      prevCards.map((card) => {
        if (card.id === id) {
          return {
            ...card,
            isFlipped: !card.isFlipped,
          };
        } else {
          return card;
        }
      })
    );
  };

  return (
    <div className="game-main">
      <div className="game-buttons">
        <button>HINT</button>
        <div className="timer-container">02:69</div>
        <button>GIVE UP</button>
      </div>
      <div className="card-container">
        {cards.map((card) => (
          <div
            className={`card1 ${
              (card.id === 4 || card.id === 5 || card.id === 6) && card.isFlipped
                ? 'rotate-text bottom-right'
                : ''
            }`}
            key={card.id}
            onClick={() => handleCardClick(card.id)}
          >
            <div className={`card-text ${card.isFlipped ? 'card-text-second' : 'card-text-back'}`}>
              {card.isFlipped ? (
                <>
                  {card.text}
                  <img src={card.sideImg} alt="Icon" />
                </>
              ) : (
                '?'
              )}
            </div>
            <div className="heart-icon">
              {card.isFlipped && (card.id === 4 || card.id === 5 || card.id === 6) ? (
                <img
                  className="bottom-right-image"
                  src={getIconForSuit(card.text)}
                  alt="Icon"
                />
              ) : null}
            </div>
            <div
              className={`card-text-first ${card.isFlipped && (card.id === 4 || card.id === 5 || card.id === 6)
                ? 'rotate-text'
                : ''}`}
            >
              {card.isFlipped ? (
                <>
                  {card.id === 4 || card.id === 5 || card.id === 6 ? (
                    <p className="special-text">{card.customText}</p>
                  ) : (
                    card.customText
                  )}
                  <img
                    className={`bottom-right-image ${
                      card.id === 4 || card.id === 5 || card.id === 6 ? 'bottom-right-custom' : ''
                    }`}
                    src={getIconForSuit(card.text)}
                    alt="Icon"
                  />
                </>
              ) : null}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Game;
