import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';
import './style/styles.css'; // Adjust the file path based on your project structure
import icon1 from './style/icon1.png'; // Adjust the file path based on your project structure
import heart from './style/heart.png';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState(null);

  const handleEmailChange = (e) => setEmail(e.target.value);

  const handlePasswordChange = (e) => setPassword(e.target.value);

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost/game/login.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
        credentials: 'omit',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();

      if (result.success) {
        console.log('Login successful');

        localStorage.setItem('loggedInUser', JSON.stringify({
          id: result.userId,
          username: result.username,
          email: email,
        }));

        sessionStorage.setItem('userId', result.userId);
        localStorage.setItem('isLoggedIn', 'true');
        navigate('/levels');
      } else {
        console.error('Login failed:', result.message);
        setLoginStatus({
          success: false,
          message: result.message,
        });
      }
    } catch (error) {
      console.error('Error:', error);
      setLoginStatus({
        success: false,
        message: 'Error during login. Please try again later.',
      });
    }
  };

  const handleGoogleLoginSuccess = async (credentialResponse) => {
    console.log('Google login successful:', credentialResponse);

    try {
      const response = await fetch('http://localhost/game/getUserId.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ credential: credentialResponse.credential }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();

      console.log('Response from server:', result);

      // Ensure that result contains userId and it's not undefined
      if (result.success && result.userId) {
        localStorage.setItem('loggedInUser', JSON.stringify({
          id: result.userId,
          username: result.username || generateRandomUsername(),
          email: result.email,
        }));
        navigate('/levels');
      } else {
        // Handle case where user ID is not present in the response
        console.error('User ID not found or undefined in the response');
        setLoginStatus({
          success: false,
          message: 'User ID not found or undefined in the response',
        });
      }
    } catch (error) {
      console.error('Error:', error);
      setLoginStatus({
        success: false,
        message: 'Error during Google login. Please try again later.',
      });
    }
  };

  const handleGoogleLoginError = (error) => {
    console.error('Google login failed:', error);
    setLoginStatus({
      success: false,
      message: 'Google login failed.',
    });
  };

  const handleRegister = () => navigate('/register');

  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem('loggedInUser'));
    if (loggedInUser) {
      console.log('Logged in user:', loggedInUser);
    }
  }, []);

  const generateRandomUsername = () => {
    const adjectives = ['Happy', 'Funny', 'Clever', 'Smart', 'Creative'];
    const nouns = ['Cat', 'Dog', 'Rabbit', 'Tiger', 'Elephant'];
    const randomAdjective = adjectives[Math.floor(Math.random() * adjectives.length)];
    const randomNoun = nouns[Math.floor(Math.random() * nouns.length)];
    return `${randomAdjective}${randomNoun}`;
  };

  return (
    <div className="login-main">
      <img className='icon1' src={icon1} alt="Icon" />
      <img className='icon2' src={icon1} alt="Icon" />
      <img className='icon3' src={icon1} alt="Icon" />
      <img className='icon4' src={icon1} alt="Icon" />
      <img className='icon5' src={heart} alt="Icon" />
      <img className='icon6' src={heart} alt="Icon" />

      <h2>Login</h2>
      <form>
        <input type="email" value={email} onChange={handleEmailChange} placeholder="Email" />
        <br />
        <input type="password" value={password} onChange={handlePasswordChange} placeholder="Password" />
        <br />
        <div className="button">
          <button type="button" onClick={handleLogin}>
            Login
          </button>
          <GoogleLogin
            clientId="301743701977-0jo8mmnfhqigsk8i1bpti9m6vv50rvgt.apps.googleusercontent.com"
            onSuccess={handleGoogleLoginSuccess}
            onError={handleGoogleLoginError}
          >
            Login with Google
          </GoogleLogin>
        </div>
      </form>

      {loginStatus && (
        <div className={`login-message ${loginStatus.success ? 'success' : 'error'}`}>
          {loginStatus.success
            ? 'Login successful'
            : loginStatus.message}
        </div>
      )}

      <div className="register-link">
        Not a user? <a href="/register" onClick={handleRegister}>Register now</a>
      </div>
    </div>
  );
};

export default Login;
