import React, { useState } from 'react';

const BackgroundSelector = ({ onSelectBackground, wallpaperOptions }) => {
  const [selectedBackground, setSelectedBackground] = useState('');

  const handleBackgroundClick = (background) => {
    setSelectedBackground(background);
    onSelectBackground(background);
  };

  return (
    <div className="background-selector">
      <h3>Choose Background</h3>
      <div className="background-options">
        {wallpaperOptions.map((background, index) => (
          <div
            key={index}
            className={`background-option ${background === selectedBackground ? 'selected' : ''}`}
            onClick={() => handleBackgroundClick(background)}
          >
            {/* Display the wallpaper as background */}
            <div
              className="background-image"
              style={{ backgroundImage: `url(${background})` }}
            ></div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BackgroundSelector;
