import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './style/styles.css'; // Adjust the file path based on your project structure

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState(null);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = async () => {
    try {
      // Simulating successful login for demonstration purposes
      // In a real application, you would perform actual authentication here
      const randomUsername = generateRandomUsername();

      // Save user session in localStorage
      localStorage.setItem('isLoggedIn', 'true');
      localStorage.setItem('username', randomUsername);

      // Redirect to the levels page
      navigate('/levels');
    } catch (error) {
      console.error('Error:', error);
      setLoginStatus({
        success: false,
        message: 'Error during login. Please try again later.',
      });
    }
  };

  const handleRegister = () => {
    // Redirect to the registration page
    navigate('/register');
  };

  const generateRandomUsername = () => {
    // Generate a random username (for demonstration purposes)
    const adjectives = ['Happy', 'Funny', 'Silly', 'Lucky', 'Clever'];
    const nouns = ['Cat', 'Dog', 'Bird', 'Fish', 'Rabbit'];
    const randomAdjective = adjectives[Math.floor(Math.random() * adjectives.length)];
    const randomNoun = nouns[Math.floor(Math.random() * nouns.length)];
    return `${randomAdjective}${randomNoun}`;
  };

  return (
    <div className="login-main">
      <h2>Login</h2>
      <form>
        <input type="email" value={email} onChange={handleEmailChange} placeholder="Email" />
        <br />
        <input type="password" value={password} onChange={handlePasswordChange} placeholder="Password" />
        <br />
        <div className="button">
          <button type="button" onClick={handleLogin}>
            Login
          </button>
        </div>
      </form>

      {loginStatus && (
        <div className={`login-message ${loginStatus.success ? 'success' : 'error'}`}>
          {loginStatus.success
            ? 'Login successful'
            : loginStatus.message}
        </div>
      )}

      <div className="register-link">
        Not a user? <a href="/register" onClick={handleRegister}>Register now</a>
      </div>
    </div>
  );
};

export default Login;
