import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './style/styles.css'; // Adjust the file path based on your project structure
import icon1 from './style/icon1.png'; // Adjust the file path based on your project structure
import heart from './style/heart.png';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState(null);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://apis.google.com/js/platform.js';
    script.onload = initGoogleSignIn;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const initGoogleSignIn = () => {
    console.log('Initializing Google Sign-In...');
    window.gapi.load('auth2', () => {
      console.log('Auth2 loaded');
      window.gapi.auth2.init({
        client_id: '916183960867-podcm2n2nil1q66r019e53o02d8obb2h.apps.googleusercontent.com',
      }).then(() => {
        console.log('Google Sign-In initialized successfully');
      }).catch((error) => {
        console.error('Error initializing Google Sign-In:', error);
      });
    });
  };


  const handleGoogleSignIn = () => {
    const auth2 = window.gapi.auth2.getAuthInstance();
    auth2.signIn().then(googleUser => {
      const profile = googleUser.getBasicProfile();
      const email = profile.getEmail();
      console.log('Logged in with Google:', email);
      // Handle Google user login, e.g., redirect, setting user data, etc.
    });
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost/game/login.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
        credentials: 'omit',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();

      if (result.success) {
        console.log('Login successful');
        localStorage.setItem('loggedInUser', JSON.stringify({
          id: result.userData.id,
          username: result.userData.username,
          email: result.userData.email,
        }));
        sessionStorage.setItem('userId', result.userData.id);
        console.log('userId:', result.userData.id);
        localStorage.setItem('isLoggedIn', 'true');
        navigate('/levels');
      } else {
        console.error('Login failed:', result.message);
      }

      setLoginStatus({
        success: result.success,
        message: result.message,
      });
    } catch (error) {
      console.error('Error:', error);

      setLoginStatus({
        success: false,
        message: 'Error during login. Please try again later.',
      });
    }
  };

  const handleRegister = () => {
    navigate('/register');
  };

  return (
    <div className="login-main">
      <h2>Login</h2>
      <form>
        <input type="email" value={email} onChange={handleEmailChange} placeholder="Email" />
        <br />
        <input type="password" value={password} onChange={handlePasswordChange} placeholder="Password" />
        <br />
        <div className="button">
          <button type="button" onClick={handleLogin}>Login</button>
          <button type="button" onClick={handleGoogleSignIn}>Login with Google</button>
        </div>
      </form>

      {loginStatus && (
        <div className={`login-message ${loginStatus.success ? 'success' : 'error'}`}>
          {loginStatus.success ? 'Login successful' : loginStatus.message}
        </div>
      )}

      <div className="register-link">
        Not a user? <a href="/register" onClick={handleRegister}>Register now</a>
      </div>
    </div>
  );
};

export default Login;
