import React, { useState, useEffect } from 'react';
import './style/profile.css';

const Profile = () => {
  // Fetch user ID from sessionStorage
  const userId = sessionStorage.getItem('userId');

  // State to store user data
  const [userData, setUserData] = useState(null);

  // State to manage edited data
  const [editedData, setEditedData] = useState({
    username: '',
    email: '',
    password: '',
    currentPassword: '', // New property for the current password
  });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`http://localhost:8888/game/getUserProfile.php?id=${userId}`, {
          method: 'GET',
          credentials: 'omit',
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          console.log('Response text:', await response.text());
          return;
        }

        const resultText = await response.text();
        console.log('Raw result text:', resultText);

        try {
          const result = JSON.parse(resultText);
          if (result.success) {
            setUserData(result.userData);
            setEditedData({
              username: result.userData.username,
              email: result.userData.email,
              password: '', // Leave it empty initially
              currentPassword: '', // Add a new property for the current password
            });
          } else {
            console.error('Failed to fetch user data:', result.message);
          }
        } catch (jsonError) {
          console.error('Error parsing JSON:', jsonError);
          console.log('Raw result text:', resultText);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    if (userId) {
      fetchUserData();
    }
  }, [userId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedData({
      ...editedData,
      [name]: value,
    });
  };

  const handleSave = async () => {
    try {
      const requestData = {
        username: editedData.username,
        email: editedData.email,
        // Include the current password only if a new password is provided
        ...(editedData.password !== '' && { currentPassword: editedData.currentPassword }),
        password: editedData.password,
      };

      const response = await fetch(`http://localhost:8888/game/updateUserProfile.php?id=${userId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'omit',
        body: JSON.stringify(requestData),
      });

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        console.log('Response text:', await response.text());
        return;
      }

      const resultText = await response.text();
      console.log('Raw result text:', resultText);

      const result = JSON.parse(resultText);

      if (result.success) {
        setUserData(result.userData);
        console.log('User data updated successfully');
      } else {
        console.error('Failed to update user data:', result.message);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  if (!userData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-main">
        <label>Username</label>
        <input type="text" name="username" value={editedData.username} onChange={handleInputChange} />

        <label>Email</label>
        <input type="text" name="email" value={editedData.email} onChange={handleInputChange} />

        <label>Current Password</label>
        <input
          type="password"
          name="currentPassword"
          value={editedData.currentPassword}
          onChange={handleInputChange}
        />

        <label>New Password</label>
        <input
          type="password"
          name="password"
          value={editedData.password}
          onChange={handleInputChange}
        />

        <button onClick={handleSave}>Save</button>
      </div>
    </div>
  );
};

export default Profile;
