// Leaderboard.jsx

import React, { useState } from 'react';
import './style/leaderboard.css';

const Leaderboard = () => {
  const leaderboardData = [
    { rank: 1, username: 'plahyboy', score: 1000 },
    { rank: 2, username: 'uwu', score: 950 },
    { rank: 4, username: 'kristaps', score: 800 },
    { rank: 5, username: 'a', score: 800 },
    { rank: 6, username: 'asd', score: 800 },
    { rank: 7, username: 'Plaasdyer3', score: 800 },
    { rank: 8, username: 'asddsa', score: 800 },
    { rank: 9, username: 'Zemmad', score: 800 },
    { rank: 10, username: 'P', score: 800 },
    // Add more data as needed
  ];

  const itemsPerPage = 5; // Adjust the number of items per page as needed
  const [currentPage, setCurrentPage] = useState(1);

  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentLeaderboardData = leaderboardData.slice(startIndex, endIndex);

  const totalPages = Math.ceil(leaderboardData.length / itemsPerPage);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className='leaderboard-container'>
    <div className="leaderboard-main">
      <h2>Leaderboard</h2>
      <div className='rank-container'>
        {currentLeaderboardData.map((entry) => (
          <div key={entry.rank} className='rank-item'>
            <div className='rank'>
              <i className="fas fa-star"></i> {entry.rank}
            </div>
            <div className='username'>{entry.username}</div>
            <div className='score'>{entry.score}</div>
          </div>
        ))}
      </div>

        <div className="pagination">
          <button
            onClick={() => handlePageChange(currentPage - 1)}
            disabled={currentPage === 1}
          >
            Previous
          </button>
          <span>{currentPage}</span>
          <button
            onClick={() => handlePageChange(currentPage + 1)}
            disabled={currentPage === totalPages}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default Leaderboard;
