// BackgroundContext.js

import React, { createContext, useContext, useState } from 'react';

const BackgroundContext = createContext();

export const BackgroundProvider = ({ children }) => {
  const [backgroundImage, setBackgroundImage] = useState('default.jpg');
  const availableBackgrounds = ['background1.jpg', 'background2.jpg', 'background3.jpg', 'background4.jpg'];

  const changeBackground = (imageName) => {
    setBackgroundImage(imageName);
  };

  return (
    <BackgroundContext.Provider value={{ backgroundImage, availableBackgrounds, changeBackground }}>
      {children}
    </BackgroundContext.Provider>
  );
};

export const useBackground = () => {
  const context = useContext(BackgroundContext);
  if (!context) {
    throw new Error('useBackground must be used within a BackgroundProvider');
  }
  return context;
};
