// Levels.js

import React from 'react';
import './style/styles.css'; // Adjust the file path based on your project structure
import Heart from './image/heart.png';
import Diamond from './image/diamond-side.png';

const Levels = () => {
  // Check if a user is logged in
  const loggedInUserData = JSON.parse(localStorage.getItem('loggedInUser'));

  // Number of levels to create
  const totalLevels = 20;

  // Generate an array with level data
  const levelsData = Array.from({ length: totalLevels }, (_, index) => ({
    id: index + 1,
    name: `Level ${index + 1}`,
    duration: (Math.random() * 5 + 1).toFixed(2), // Random duration for demonstration
  }));

  return (
    <div className="level-main">
      {levelsData.map((level) => (
        <div key={level.id} className="level-container">
          <h1>{level.name}</h1>
          <p>{level.duration} minutes</p>
        </div>
      ))}
    </div>
  );
};

export default Levels;
