import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import NavBar from './Nav';
import Login from './Login';
import Game from './game';
import Level from './Level';
import Register from './Register';
import Profile from './Profile';
import Leaderboard from './Leaderboards';
import Stats from './Stats';
import GameLevelTwo from './GameLevelTwo';
import { BackgroundProvider } from './BackgroundContext'; // Import BackgroundProvider
import {black} from './wallpapers/black.jpg';

// Custom wrapper component to conditionally render background
const BackgroundWrapper = ({ children }) => {
  const { pathname } = window.location;
  // Exclude background on login and register pages
  const excludeBackground = ['/login', '/register'].includes(pathname);

  return (
    <div style={{ backgroundImage: excludeBackground ? 'none' : 'url(your-default-background.jpg)' }}>
      {children}
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <BackgroundProvider>
        <Routes>
          <Route
            path="/*"
            element={
              <BackgroundWrapper>
                <NavBar />
              </BackgroundWrapper>
            }
          />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/levels" element={<Level />} />
          <Route path="/game" element={<Game />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/leaderboard" element={<Leaderboard />} />
          <Route path="/stats" element={<Stats />} />
          <Route path="/gametwo" element={<GameLevelTwo />} />
          <Route path="/" element={<Navigate to="/login" />} />
        </Routes>
      </BackgroundProvider>
    </Router>
  );
};

export default App;
