import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './style/styles.css'; // Adjust the file path based on your project structure

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState(null);

  useEffect(() => {
    // Initialize Google Sign-In after the component mounts
    window.gapi.load('auth2', () => {
      const auth2 = window.gapi.auth2.init({
        client_id: 'YOUR_CLIENT_ID.apps.googleusercontent.com',
      });

      auth2.attachClickHandler('google-login-button', {}, googleSignInCallback);
    });
  }, []);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost/game/login.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
        credentials: 'omit',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();

      if (result.success) {
        console.log('Login successful');
        localStorage.setItem('loggedInUser', JSON.stringify({
          id: result.userData.id,
          username: result.userData.username,
          email: result.userData.email,
        }));
        sessionStorage.setItem('userId', result.userData.id);
        console.log('userId:', result.userData.id);
        localStorage.setItem('isLoggedIn', 'true');
        navigate('/levels');
      } else {
        console.error('Login failed:', result.message);
      }

      setLoginStatus({
        success: result.success,
        message: result.message,
      });
    } catch (error) {
      console.error('Error:', error);
      setLoginStatus({
        success: false,
        message: 'Error during login. Please try again later.',
      });
    }
  };

  const googleSignInCallback = async (googleUser) => {
    const profile = googleUser.getBasicProfile();
    const email = profile.getEmail();

    try {
      const response = await fetch('http://localhost/game/google-login.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
        credentials: 'omit',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();

      if (result.success) {
        console.log('Google login successful');
        // Handle successful login
      } else {
        console.error('Google login failed:', result.message);
        // Handle login failure
      }
    } catch (error) {
      console.error('Error during Google login:', error);
      // Handle error
    }
  };

  const handleRegister = () => {
    navigate('/register');
  };

  return (
    <div className="login-main">
      <h2>Login</h2>
      <form>
        <input type="email" value={email} onChange={handleEmailChange} placeholder="Email" />
        <br />
        <input type="password" value={password} onChange={handlePasswordChange} placeholder="Password" />
        <br />
        <div className="button">
          <button type="button" onClick={handleLogin}>Login</button>
        </div>
      </form>

      {loginStatus && (
        <div className={`login-message ${loginStatus.success ? 'success' : 'error'}`}>
          {loginStatus.success ? 'Login successful' : loginStatus.message}
        </div>
      )}

      <div className="register-link">
        Not a user? <a href="/register" onClick={handleRegister}>Register now</a>
      </div>

      {/* Google Sign-In button */}
      <div id="google-login-button">Sign in with Google</div>
    </div>
  );
};

export default Login;
