import React, { useState, useEffect } from 'react';
import './style/game.css'; // Adjust the file path based on your project structure

const Game = () => {
  const [cards, setCards] = useState([
    { id: 1, text: 'A', customText: 'A', isFlipped: false },
    { id: 2, text: 'A', customText: 'A', isFlipped: false },
    { id: 3, text: 'A', customText: 'A', isFlipped: false },
    { id: 4, text: 'A', customText: 'A', isFlipped: false },
    { id: 5, text: 'A', customText: 'A', isFlipped: false },
    { id: 6, text: 'A', customText: 'A', isFlipped: false },
    { id: 7, text: 'A', customText: 'A', isFlipped: false },
    { id: 8, text: 'A', customText: 'A', isFlipped: false },
    { id: 9, text: 'A', customText: 'A', isFlipped: false },
    { id: 10, text: 'A', customText: 'A', isFlipped: false },
    { id: 11, text: 'A', customText: 'A', isFlipped: false },
    { id: 12, text: 'A', customText: 'A', isFlipped: false },
  ]);

  useEffect(() => {
    setCards((cards) => shuffleArray(cards));
  }, []);

  function shuffleArray(array) {
    const shuffledArray = array.slice(); // Create a copy of the array to avoid mutating the original array
    for (let i = shuffledArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
    }
    return shuffledArray;
  }

  const handleCardClick = (id) => {
    setCards((prevCards) =>
      prevCards.map((card) => (card.id === id ? { ...card, isFlipped: !card.isFlipped } : card))
    );
  };

  return (
    <div className="game-main">
      <div className="game-buttons">
        <button>HINT</button>
        <div className="timer-container">02:69</div>
        <button>GIVE UP</button>
      </div>
      <div className="card-container">
        {cards.map((card) => (
          <div
            className={`card1 ${
              (card.id === 4 || card.id === 5 || card.id === 6) && card.isFlipped
                ? 'rotate-text bottom-right'
                : ''
            }`}
            key={card.id}
            onClick={() => handleCardClick(card.id)}
          >
            <div className={`card-text ${card.isFlipped ? 'card-text-second' : 'card-text-back'}`}>
              {card.isFlipped ? (
                <>
                  {card.text}
                </>
              ) : (
                '?'
              )}
            </div>
            <div className="heart-icon">
              {/* No image in the back */}
            </div>
            <div
              className={`card-text-first ${card.isFlipped && (card.id === 4 || card.id === 5 || card.id === 6)
                ? 'rotate-text'
                : ''}`}
            >
              {card.isFlipped ? (
                <>
                  {card.id === 4 || card.id === 5 || card.id === 6 ? (
                    <p className="special-text">{card.customText}</p>
                  ) : (
                    card.customText
                  )}
                </>
              ) : null}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Game;
