// BackgroundContext.js

import React, { createContext, useContext, useState } from 'react';

const BackgroundContext = createContext();

export const BackgroundProvider = ({ children }) => {
  const [background, setBackground] = useState('default.jpg'); // Set a default wallpaper
  const availableBackgrounds = ['wallpapers/wallpaper1.jpg', 'wallpapers/wallpaper2.jpg', 'wallpapers/wallpaper3.jpg'];

  const changeBackground = (newBackground) => {
    setBackground(newBackground);
  };

  return (
    <BackgroundContext.Provider value={{ background, availableBackgrounds, changeBackground }}>
      {children}
    </BackgroundContext.Provider>
  );
};

export const useBackground = () => {
  return useContext(BackgroundContext);
};
