// BackgroundContext.js

import React, { createContext, useContext, useState } from 'react';

const BackgroundContext = createContext();

export const BackgroundProvider = ({ children }) => {
  const [background, setBackground] = useState('default-background.jpg'); // Set default background

  const changeBackground = (newBackground) => {
    setBackground(newBackground);
  };

  return (
    <BackgroundContext.Provider value={{ background, changeBackground }}>
      {children}
    </BackgroundContext.Provider>
  );
};

export const useBackground = () => {
  const context = useContext(BackgroundContext);
  if (!context) {
    throw new Error('useBackground must be used within a BackgroundProvider');
  }
  return context;
};
