import React from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import NavBar from './Nav';
import Login from './Login';
import Game from './game';
import Level from './Level';
import Register from './Register';
import Profile from './Profile';
import Leaderboard from './Leaderboards';
import Stats from './Stats';
import GameLevelTwo from './GameLevelTwo';
import { BackgroundProvider } from './BackgroundContext'; // Import BackgroundProvider

const App = () => {
  const navigate = useNavigate();

  const handleBackgroundChange = (newBackground) => {
    // Conditionally change the background only if not on the Login or Register pages
    if (!['/login', '/register'].includes(window.location.pathname)) {
      // Change the background
      // Add the logic to change the background as needed
    }

    // Navigate to the clicked route
    navigate(newBackground);
  };

  return (
    <Router>
      <BackgroundProvider>
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/levels"
            element={
              <>
                <NavBar />
                <Level />
              </>
            }
          />
          <Route
            path="/game"
            element={
              <>
                <NavBar />
                <Game />
              </>
            }
          />
          <Route
            path="/profile"
            element={
              <>
                <NavBar />
                <Profile />
              </>
            }
          />
          <Route
            path="/leaderboard"
            element={
              <>
                <NavBar />
                <Leaderboard />
              </>
            }
          />
          <Route
            path="/stats"
            element={
              <>
                <NavBar />
                <Stats />
              </>
            }
          />
          <Route
            path="/gametwo"
            element={
              <>
                <NavBar />
                <GameLevelTwo />
              </>
            }
          />
        </Routes>
      </BackgroundProvider>
    </Router>
  );
};

export default App;
