// BackgroundSelector.jsx

import React from 'react';

const BackgroundSelector = ({ onSelectBackground, backgroundOptions, selectedBackground }) => {
  const handleBackgroundChange = (event) => {
    const selectedBackground = event.target.value;
    onSelectBackground(selectedBackground);
  };

  return (
    <div className="background-selector">
      <h3>Choose Background</h3>
      <select value={selectedBackground} onChange={handleBackgroundChange}>
        {backgroundOptions.map((background, index) => (
          <option key={index} value={background}>
            {background}
          </option>
        ))}
      </select>
    </div>
  );
};

export default BackgroundSelector;
