import React, { useState, useEffect } from 'react';
import './style/Nav.css';
import wallpaper from './wallpapers/wallpaper7.jpg';
import wallpaper1 from './wallpapers/wallpaper10.jpg';
import wallpaper2 from './wallpapers/wallpaper8.jpg';
import wallpaper3 from './wallpapers/wallpaper.jpg';

const MainMenu = () => {
  const [selectedBackground, setSelectedBackground] = useState('');
  const [isDarkTheme, setIsDarkTheme] = useState(() => {
    return localStorage.getItem('isDarkTheme') === 'true' ? true : false;
  });
  const [userCoins, setUserCoins] = useState(0);
  const [showBackgroundDropdown, setShowBackgroundDropdown] = useState(false);

  useEffect(() => {
    const fetchUserCoins = async () => {
      try {
        const loggedInUserData = JSON.parse(localStorage.getItem('loggedInUser'));

        if (!loggedInUserData || !loggedInUserData.id) {
          console.log('User data not available. Skipping fetchUserCoins.');
          return;
        }

        const userId = loggedInUserData.id;
        const url = `http://localhost:8888/game/getUserCoins.php?userId=${userId}`;

        const response = await fetch(url, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
          mode: 'cors',
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          console.log('Response text:', await response.text());
          return;
        }

        const result = await response.json();

        if (result.success) {
          setUserCoins(result.coins);
        } else {
          console.error('Failed to fetch user coins:', result.message);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUserCoins();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('loggedInUser');
    localStorage.removeItem('isLoggedIn');
    window.location.href = '/login';
  };

  const toggleTheme = () => {
    const newThemeState = !isDarkTheme;
    setIsDarkTheme(newThemeState);
    localStorage.setItem('isDarkTheme', newThemeState.toString());
  };

  const handleBackgroundChange = (background) => {
    localStorage.setItem('selectedBackground', background);
    setSelectedBackground(background);
  };

  return (
    <div>
      <div className={`area${isDarkTheme ? ' dark-theme' : ''}`}></div>
      <nav className={`main-menu${isDarkTheme ? ' dark-theme' : ''}`}>
        <ul>
          <li>
            <a href="https://jbfarrow.com">
              <i className="fa fa-home fa-2x"></i>
              <span className="nav-text">Main Page</span>
            </a>
          </li>
          <li className="has-subnav">
            <a href="/levels">
              <i className="fa fa-globe fa-2x"></i>
              <span className="nav-text">View All Levels</span>
            </a>
          </li>
          <li>
            <a href="#">
              <i className="fa fa-signal fa-2x"></i>
              <span className="nav-text">Game History</span>
            </a>
          </li>
          <li>
            <a href="/leaderboard">
              <i className="fa fa-trophy" aria-hidden="true"></i>
              <span className="nav-text">LeaderBoard</span>
            </a>
          </li>
          <li>
            <a href="/profile">
              <i className="fa fa-gear fa-2x"></i>
              <span className="nav-text">Profile</span>
            </a>
          </li>
          <li>
            <a href="#">
              <i className="fa fa-heart fa-2x"></i>
              <span className="nav-text">Credits: {userCoins} </span>
            </a>
          </li>
          <li className="has-subnav">
  <a href="#">
    <i className="fa fa-picture-o fa-2x"></i>
    <span className="nav-text">Change Background</span>
  </a>
  <ul className="subnav">
    <li onMouseEnter={() => setShowBackgroundDropdown(true)}> <a href="#" onClick={() => handleBackgroundChange(wallpaper)}>Wallpaper 1</a>
    </li>
    <li onMouseEnter={() => setShowBackgroundDropdown(true)}> <a href="#" onClick={() => handleBackgroundChange(wallpaper1)}>Wallpaper 2</a>
    </li>
    </ul>
</li>

        </ul>
        <ul className="logout">
          <li>
            <a href="#" onClick={toggleTheme}>
              <i className="fa fa-adjust fa-2x"></i>
              <span className="nav-text">Toggle Theme</span>
            </a>
          </li>
          <li>
            <a href="#" onClick={handleLogout}>
              <i className="fa fa-power-off fa-2x"></i>
              <span className="nav-text">Logout</span>
            </a>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default MainMenu;
