// Profile.js

import React, { useState, useEffect } from 'react';
import './style/profile.css';
import { useBackground } from './BackgroundContext';
import wallpaper from './wallpapers/wallpaper1.jpg';
import wallpaper1 from './wallpapers/wallpaper2.jpg';
import wallpaper2 from './wallpapers/wallpaper3.jpg';
import wallpaper3 from './wallpapers/wallpaper5.jpg';

const Profile = () => {
  const userId = sessionStorage.getItem('userId');
  const { changeBackground } = useBackground();
  const [userData, setUserData] = useState(null);
  const [editedData, setEditedData] = useState({
    username: '',
    email: '',
    password: '',
  });
  const [validationErrors, setValidationErrors] = useState({
    username: '',
    email: '',
    password: '',
  });
  const [successMessage, setSuccessMessage] = useState('');
  const [duplicateUserError, setDuplicateUserError] = useState('');
  const [selectedBackground, setSelectedBackground] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`http://localhost:8888/game/getUserProfile.php?id=${userId}`, {
          method: 'GET',
          credentials: 'omit',
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          console.log('Response text:', await response.text());
          return;
        }

        const resultText = await response.text();
        console.log('Raw result text (fetchUserData):', resultText);

        try {
          const result = JSON.parse(resultText);
          if (result.success) {
            setUserData(result.userData);
            setEditedData({
              username: result.userData.username,
              email: result.userData.email,
              password: '', // Do not include the password initially
            });
          } else {
            console.error('Failed to fetch user data:', result.message);
          }
        } catch (jsonError) {
          console.error('Error parsing JSON:', jsonError);
          console.log('Raw result text (fetchUserData):', resultText);
        }
      } catch (error) {
        console.error('Error (fetchUserData):', error);
      }
    };

    if (userId) {
      fetchUserData();
    }
  }, [userId]);

  useEffect(() => {
    // Load the previously selected background from localStorage
    const storedBackground = localStorage.getItem('selectedBackground');
    console.log('Stored Background:', storedBackground);
    if (storedBackground) {
      setSelectedBackground(storedBackground);
      changeBackground(storedBackground);
    }
  }, [changeBackground]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedData({
      ...editedData,
      [name]: value,
    });

    setValidationErrors({
      ...validationErrors,
      [name]: '',
    });

    setSuccessMessage('');
    setDuplicateUserError('');
  };

  const validateInputs = () => {
    let valid = true;
    const errors = {};

    if (!editedData.username.trim()) {
      valid = false;
      errors.username = 'Username cannot be empty';
    }

    if (!editedData.email.trim()) {
      valid = false;
      errors.email = 'Email cannot be empty';
    }

    if (!editedData.password.trim()) {
      valid = false;
      errors.password = 'Password cannot be empty';
    }

    if (!editedData.email.includes('@')) {
      valid = false;
      errors.email = 'Invalid email format';
    }

    if (editedData.password.length < 7) {
      valid = false;
      errors.password = 'Password must be at least 7 characters';
    }

    if (!/^[a-zA-Z]+$/.test(editedData.username)) {
      valid = false;
      errors.username = 'Username can only contain Latin alphabet characters';
    }

    setValidationErrors(errors);

    return valid;
  };

  const handleSave = async () => {
    try {
      if (!validateInputs()) {
        console.error('Validation failed. Please check the form for errors.');
        return;
      }

      const requestData = {
        username: editedData.username,
        email: editedData.email,
        ...(editedData.password && { password: editedData.password }),
      };

      const response = await fetch(`http://localhost:8888/game/updateUserProfile.php?id=${userId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'omit',
        body: JSON.stringify(requestData),
      });

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        console.log('Response text (handleSave):', await response.text());
        return;
      }

      const resultText = await response.text();
      console.log('Raw result text (handleSave):', resultText);

      const result = JSON.parse(resultText);

      if (result.success) {
        setUserData(result.userData);
        setSuccessMessage('Profile data updated successfully');
        console.log('User data updated successfully');
      } else {
        if (result.errorCode === 'DUPLICATE_USER') {
          setDuplicateUserError(result.message);
        } else {
          console.error('Failed to update user data:', result.message);
        }
      }
    } catch (error) {
      console.error('Error (handleSave):', error);
    }
  };

  const handleBackgroundChange = (newBackground) => {
    setSelectedBackground(newBackground);
    changeBackground(newBackground);

    // Save the selected background to localStorage without /static/media/
    const localStorageBackground = newBackground.replace(/^.*[\\\/]/, ''); // Extract the file name
    localStorage.setItem('selectedBackground', localStorageBackground);

    // Dynamically set the selected background for the body
    document.body.style.backgroundImage = `url(${newBackground})`;
    document.body.style.backgroundSize = 'cover';
    document.body.style.backgroundPosition = 'center';
    document.body.style.backgroundRepeat = 'no-repeat';
  };


  if (!userData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-main">
        <label>Username</label>
        <input type="text" name="username" value={editedData.username} onChange={handleInputChange} />
        <div className="error-message">{validationErrors.username}</div>

        <label>Email</label>
        <input type="text" name="email" value={editedData.email} onChange={handleInputChange} />
        <div className="error-message">{validationErrors.email}</div>

        <label>Password</label>
        <input type="password" name="password" value={editedData.password} onChange={handleInputChange} />
        <div className="error-message">{validationErrors.password}</div>

        {duplicateUserError && <div className="error-message">{duplicateUserError}</div>}
        {successMessage && <div className="success-message">{successMessage}</div>}

        <label>Background</label>
        <div className='background-container'>
          <div
            className='a-background'
            style={{
              backgroundImage: `url(${wallpaper})`,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
            }}
            onClick={() => handleBackgroundChange(wallpaper)}
          ></div>
          <div
            className='a-background'
            style={{
              backgroundImage: `url(${wallpaper1})`,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
            }}
            onClick={() => handleBackgroundChange(wallpaper1)}
          ></div>
          <div
            className='a-background'
            style={{
              backgroundImage: `url(${wallpaper2})`,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
            }}
            onClick={() => handleBackgroundChange(wallpaper2)}
          ></div>
          <div
            className='a-background'
            style={{
              backgroundImage: `url(${wallpaper3})`,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
            }}
            onClick={() => handleBackgroundChange(wallpaper3)}
          ></div>
        </div>
        <button onClick={handleSave}>Save</button>
      </div>
    </div>
  );
};

export default Profile;
