import React, { useState, useEffect } from 'react';
import './style/game.css'; // Adjust the file path based on your project structure
import hearts from './image/heart.png'
import heart164 from './image/sirds.png'
import spade from './image/spade.png'
import diamond from './image/diamond.png'
import diamondSide from './image/diamond-side.png'
import Clubs from './image/clubs.png'

const Game = () => {
  const [cards, setCards] = useState([
    { id: 1, img: hearts, alt: "Icon", text: "A", sideImg: heart164, customText: "A" },
    { id: 2, img: hearts, alt: "Icon", text: "A", sideImg: heart164, customText: "A" },
    { id: 3, img: hearts, alt: "Icon", text: "A", sideImg: heart164, customText: "A" },
    { id: 4, img: spade, alt: "Icon", text: "A", sideImg: spade, customText: "A" },
    { id: 5, img: spade, alt: "Icon", text: "A", sideImg: spade, customText: "A" },
    { id: 6, img: spade, alt: "Icon", text: "A", sideImg: spade, customText: "A" },
    { id: 7, img: diamond, alt: "Icon", text: "A", sideImg: diamondSide, customText: "A" },
    { id: 8, img: diamond, alt: "Icon", text: "A", sideImg: diamondSide, customText: "A" },
    { id: 9, img: diamond, alt: "Icon", text: "A", sideImg: diamondSide, customText: "A" },
    { id: 10, img: Clubs, alt: "Icon", text: "A", sideImg: Clubs, customText: "A" },
    { id: 11, img: Clubs, alt: "Icon", text: "A", sideImg: Clubs, customText: "A" },
    { id: 12, img: Clubs, alt: "Icon", text: "A", sideImg: Clubs, customText: "A" },
  ]);

  useEffect(() => {
    setCards((cards) => shuffleArray(cards));
  }, []);

  function shuffleArray(array) {
    let i = array.length - 1;
    for (; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      const temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
    return array;
  }

  return (
    <div className="game-main">
    <div className='game-buttons'>
      <button>HINT</button>
      <div className="timer-container">
        02:69
      </div>
      <button>GIVE UP</button>
    </div>
    <div className="card-container">
      {cards.map((card) => (
        <div className={`card1 ${card.id === 4 || card.id === 5 || card.id === 6 ? 'bottom-right' : ''}`} key={card.id}>
          <div className={card.id === 4 || card.id === 5 || card.id === 6 ? "special-text" : "card-text-second"}>
            {card.text}
            <img src={card.sideImg} />
          </div>
          <div className="heart-icon">
            <img
              className={`hearts ${card.id === 4 || card.id === 5 || card.id === 6 ? 'bottom-right-rotate' : ''}`}
              src={card.img}
              alt={card.alt}
            />
          </div>
          <div className={`card-text-first ${card.id === 4 || card.id === 5 || card.id === 6 ? 'rotate-text' : ''}`}>
            {card.id === 4 || card.id === 5 || card.id === 6 ? (
              <p className="special-text">{card.customText}</p>
            ) : (
              card.customText
            )}
           <img
  className={`bottom-right-image ${card.id === 4 || card.id === 5 || card.id === 6 ? 'bottom-right-custom' : ''}`}
  src={card.sideImg}
  alt={card.alt}
/>

          </div>
        </div>
      ))}
    </div>
  </div>
  );
};

export default Game;
