import React, { useState, useEffect } from 'react';
import './style/game.css'; // Adjust the file path based on your project structure
import hearts from './image/heart.png';
import heart164 from './image/sirds.png';
import spade from './image/spade.png';
import diamond from './image/diamond.png';
import diamondSide from './image/diamond-side.png';
import Clubs from './image/clubs.png';

const cardTypes = [
  { id: 1, img: hearts, text: 'A', sideImg: heart164, customText: 'A' },
  { id: 2, img: spade, text: 'A', sideImg: spade, customText: 'A' },
  { id: 3, img: diamond, text: 'A', sideImg: diamondSide, customText: 'A' },
  { id: 4, img: Clubs, text: 'A', sideImg: Clubs, customText: 'A' },
];

const Game = () => {
  const [cards, setCards] = useState([]);
  const [isGameFinished, setIsGameFinished] = useState(false);
  const [correctMatches, setCorrectMatches] = useState(0);
  const [selectedCardIds, setSelectedCardIds] = useState([]);
  const [incorrectAttempts, setIncorrectAttempts] = useState(0);

  useEffect(() => {
    resetGame();
  }, []); // Initial setup

  useEffect(() => {
    if (correctMatches === cardTypes.length * 2) {
      setIsGameFinished(true);
      // Handle game completion (e.g., show a message or perform any other action)
    }
  }, [correctMatches]);

  const resetGame = () => {
    const initialCards = [];
    const totalPairs = cardTypes.length * 3; // Ensure we have 3 pairs (6 cards) of identical cards

    for (let i = 0; i < totalPairs; i++) {
      const type = cardTypes[i % cardTypes.length];

      // Create two cards for each type in a pair
      initialCards.push({
        ...type,
        id: i * 2 + 1,
        isFlipped: false,
        matched: false,
      });

      initialCards.push({
        ...type,
        id: i * 2 + 2,
        isFlipped: false,
        matched: false,
      });
    }

    setCards(shuffleArray(initialCards));
    setIsGameFinished(false);
    setCorrectMatches(0);
    setSelectedCardIds([]);
    setIncorrectAttempts(0);
  };

  const shuffleArray = (array) => {
    const shuffledArray = array.slice();
    for (let i = shuffledArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
    }
    return shuffledArray;
  };

  const handleCardClick = (id) => {
    if (isGameFinished || selectedCardIds.length >= 3) {
      // Game is already finished or 3 cards are already selected, do nothing
      return;
    }

    // Find the clicked card
    const clickedCard = cards.find((card) => card.id === id);

    // Check if the clicked card is already flipped or matched
    if (clickedCard.isFlipped || clickedCard.matched) {
      // If the card is already flipped or matched, do nothing
      return;
    }

    // Flip the clicked card
    setCards((prevCards) =>
      prevCards.map((card) => (card.id === id ? { ...card, isFlipped: true } : card))
    );

    // Add the clicked card to the selected cards
    setSelectedCardIds((prevSelected) => [...prevSelected, id]);

    // Check if three cards are selected
    if (selectedCardIds.length === 2) {
      const selectedCardTypes = selectedCardIds.map((cardId) => {
        const selectedCard = cards.find((card) => card.id === cardId);
        return selectedCard.customText;
      });

      // Check if all selected cards are spades
      const areAllSpades = selectedCardTypes.every((type) => type === 'A');

      if (!areAllSpades) {
        // Increase incorrect attempts and check if two consecutive incorrect attempts have been made
        setIncorrectAttempts((prevAttempts) => {
          const updatedAttempts = prevAttempts + 1;
          if (updatedAttempts >= 2) {
            setTimeout(() => {
              setCards((prevCards) =>
                prevCards.map((card) =>
                  selectedCardIds.includes(card.id) ? { ...card, isFlipped: false } : card
                )
              );
              setSelectedCardIds([]); // Reset selected cards after flipping back
              setIncorrectAttempts(0); // Reset incorrect attempts after flipping back
            }, 1000); // Delay for a moment before flipping back
          }
          return updatedAttempts;
        });
      } else {
        // Reset selected cards, incorrect attempts, and selected non-spade count on a correct match
        setCards((prevCards) =>
          prevCards.map((card) =>
            selectedCardIds.includes(card.id) ? { ...card, matched: true } : card
          )
        );
        setCorrectMatches((prevMatches) => prevMatches + 2); // Increase by 2 for each correct match
        setSelectedCardIds([]);
        setIncorrectAttempts(0);
      }
    }
  };

  const handleGiveUp = () => {
    // Reset the game when the user gives up
    resetGame();
  };

  return (
    <div className="game-main">
      <div className="game-buttons">
        <button>HINT</button>
        <div className="timer-container">02:69</div>
        <button onClick={handleGiveUp}>GIVE UP</button>
      </div>
      <div className="card-container">
        {cards.map((card) => (
          <div
            className={`card1 ${card.isFlipped ? 'flipped' : ''}`}
            key={card.id}
            onClick={() => handleCardClick(card.id)}
          >
            <div
              className={`card-text ${card.isFlipped ? 'card-text-second' : 'card-text-back'} ${
                card.id === 4 || card.id === 5 || card.id === 6 ? 'spade-specific-styling' : ''
              }`}
            >
              {card.isFlipped ? (
                <>
                  {card.text}
                  <img src={card.sideImg} alt={card.alt} />
                </>
              ) : (
                '?'
              )}
            </div>
            <div className={`heart-icon ${card.isFlipped ? 'bottom-right-rotate' : ''}`}>
              <img
                className={`hearts ${card.isFlipped ? 'bottom-right-rotate' : ''}`}
                src={card.isFlipped ? card.img : ''}
                alt={card.alt}
              />
            </div>
            <div
              className={`card-text-first ${card.isFlipped ? 'rotate-text' : ''} ${
                card.id === 4 || card.id === 5 || card.id === 6 ? 'special-text' : ''
              } ${
                card.id === 4
                  ? 'top-left-rotate'
                  : card.id === 5
                  ? 'top-left-rotate'
                  : card.id === 6
                  ? 'top-left-rotate'
                  : ''
              }`}
            >
              {card.isFlipped ? (
                <>
                  {card.id === 4 || card.id === 5 || card.id === 6 ? (
                    <p className="special-text">{card.customText}</p>
                  ) : (
                    card.customText
                  )}
                  <img
                    className={`bottom-right-image ${
                      card.id === 4 || card.id === 5 || card.id === 6 ? 'bottom-right-custom' : ''
                    }`}
                    src={card.isFlipped ? card.sideImg : ''}
                    alt={card.alt}
                  />
                </>
              ) : null}
            </div>
            {/* Add condition to apply border class only when not flipped */}
            <div className={`border-container ${!card.isFlipped ? 'no-border' : ''}`}>
              {/* Your border content here */}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Game;
