import React, { useState, useEffect } from 'react';
import './style/profile.css';

const Profile = () => {
  // Fetch user information from local storage
  const loggedInUserData = JSON.parse(localStorage.getItem('loggedInUser'));

  // Initialize state with user data
  const [username, setUsername] = useState(loggedInUserData?.username || '');
  const [email, setEmail] = useState(loggedInUserData?.email || '');
  const [password, setPassword] = useState(loggedInUserData?.password || '');

  // Update state if user data changes
  useEffect(() => {
    setUsername(loggedInUserData?.username || '');
    setEmail(loggedInUserData?.email || '');
    setPassword(loggedInUserData?.password || '');
  }, [loggedInUserData]);

  return (
    <div className="profile-container">
      <div className="profile-main">
        <label>Username</label>
        <input type="text" value={username} readOnly />

        <label>Email</label>
        <input type="text" value={email} readOnly />

        <label>Password</label>
        <input type="password" value={password} readOnly />

        <button>Save</button>
      </div>
    </div>
  );
};

export default Profile;
