// Card.js
import React from 'react';
import './style/game.css'; // Adjust the file path based on your project structure

const Card = ({ value, suit, img }) => {
  return (
    <div className="card1">
      <div className="card-text-second">
        {value}
        {suit === 'heart' && <img className="hearts" src={img} alt="Icon" />}
      </div>
      <div className="icon-container">
        <img className="card-icon" src={img} alt="Icon" />
      </div>
      <div className="card-text-first">
        {value}
        {suit === 'heart' && <img className="hearts" src={img} alt="Icon" />}
      </div>
    </div>
  );
};

export default Card;
